	if err != nil {
		return err
	}

	if _, err = io.Copy(w, reader); err != nil {
		return errors.Wrap(err, "failed to copy contents")
	}

	if !validator.validate() {
		return errors.New("checksum validation failed")
	}

	return nil
}

