	ticker := make(chan time.Time, 1)

	go func() {
		defer close(ticker)

		for {
			select {
			case <-ctx.Done():
				return
			default:
				time.Sleep(randomDuration(start, end))
				sendNow(ticker)
			}
		}
	}()

	return ticker
}

