func (td *TransitionDependenciesMap) UnmarshalJSON(b []byte) error {
	depMap := make(map[apicontainerstatus.ContainerStatus]TransitionDependencySet)
	err := json.Unmarshal(b, &depMap)
	if err == nil {
		*td = depMap
		return nil
